import React,{useState,useEffect} from 'react'
import Logo from '../assets/logo.svg'
const Contact = ({contacts,currentUser}) => {
   
    const[currentUserName,setCurrentUserName] = useState(undefined)
    const[currentUserImage,setcurrentUserImage] = useState(undefined)

    useEffect(()=>{
      
         if(currentUser){
            setCurrentUserName(currentUser.name);
            setcurrentUserImage(currentUser.avatarImage);
         }
         
    },[currentUser])
     
  return (
    <>
           {/* {currentUserName && currentUserImage &&( */}

               <div>
                   <div className='flex justify-center items-center uppercase'>
                        <img src={Logo} alt="logo" className='h-[8vh] w-[5vw]'/>
                         <h1>chatme</h1>
                        
                   </div>

                   <div className=''>
                  {
                     contacts.map((contact,index)=>{
                        return(
                            <div key={contact._id} onClick={()=>{}}>
                            <div className=''>
                               <img  src={`data:image/svg+xml;base64,${contact.avatarImage}`}
                                alt={`Avatar ${index}`}/>
                            </div>
                            <div className=''>
                                <h3>{contact.username}</h3>
                            </div>
                            </div>
                        )
                     })
                  }

                   </div>
                   <div className=''>
                       <img  src={`data:image/svg+xml;base64,${currentUser.avatarImage}`}
                       alt={`Avatar`}/>
                   </div>
                   <div>
                    <h1>{currentUserName}</h1>
                   </div>
               </div>
           {/* )} */}
    </>
    
  )
}

export default Contact
