import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { AllusersRoute } from "../utils/APIRouts";

const Chatt = () => {
  const navigate = useNavigate();

  const [contacts, setContacts] = useState([]);
   const[currentUser,setCurrentuser] = useState(undefined);
  useEffect(() => { 

     const functt = async ()=>{
        if (!localStorage.getItem("chat-app-user"))
            {
                navigate("/login")
            }else{
               setCurrentuser(await JSON.parse(localStorage.getItem("chat-app-user")));
            }
     }
     functt();
  }, [navigate]);

  useEffect(() => {

    const fetch = async () => {
      try {
         if(currentUser.isAvatarImageSet){
            const user = await axios.get(`${AllusersRoute}/${currentUser._id}`);
            setContacts(user.data);
         }else{
            navigate("/setAvatar");
         }
       
      } catch (error) {
        console.log(error);
      }
    };
    if(currentUser){
        fetch();
    }
   
  }, [currentUser]);

  return (
    <div>
      <div className="bg-[#131324] w-[100vw] h-[100vh] flex flex-col justify-center items-center">
        <div className="bg-[#00000076] h-[85vh] w-[85vw] grid grid-cols-[25%_75%]">
          
        </div>
      </div>
    </div>
  );
};

export default Chatt;
